    21→// NewClient creates a new analysis client.
    22→func NewClient(baseURL string) *Client {
    23→	if baseURL == "" {
    24→		baseURL = "http://localhost:8080"
    25→	}
    26→	return &Client{
    27→		baseURL: baseURL,
    28→		httpClient: &http.Client{
    29→			Timeout: 60 * time.Second,
