    83→// AnalyzeGeometry performs Kakeya-inspired analysis on a set of vectors.
    84→func (c *Client) AnalyzeGeometry(ctx context.Context, vectors [][]float64) (*GeometryResult, error) {
    85→	if len(vectors) < 3 {
    86→		return nil, fmt.Errorf("need at least 3 vectors, got %d", len(vectors))
    87→	}
    88→
    89→	reqBody := map[string]any{
    90→		"vectors": vectors,
    91→	}
    92→
    93→	body, err := json.Marshal(reqBody)
    94→	if err != nil {
    95→		return nil, err
    96→	}
    97→
    98→	req, err := http.NewRequestWithContext(ctx, "POST", c.baseURL+"/analyze/geometry", bytes.NewReader(body))
    99→	if err != nil {
   100→		return nil, err
   101→	}
   102→	req.Header.Set("Content-Type", "application/json")
   103→
   104→	resp, err := c.httpClient.Do(req)
   105→	if err != nil {
   106→		return nil, err
   107→	}
   108→	defer resp.Body.Close()
   109→
   110→	respBody, err := io.ReadAll(resp.Body)
   111→	if err != nil {
   112→		return nil, err
   113→	}
   114→
   115→	if resp.StatusCode != http.StatusOK {
   116→		return nil, fmt.Errorf("analysis failed (status %d): %s", resp.StatusCode, string(respBody))
   117→	}
   118→
   119→	var result GeometryResult
   120→	if err := json.Unmarshal(respBody, &result); err != nil {
   121→		return nil, fmt.Errorf("decode response: %w", err)
   122→	}
   123→
   124→	return &result, nil
   125→}
