   127→// CompareBilateral compares geometric properties between two vector sets.
   128→func (c *Client) CompareBilateral(ctx context.Context, sender, receiver [][]float64) (*BilateralResult, error) {
   129→	if len(sender) < 3 {
   130→		return nil, fmt.Errorf("need at least 3 sender vectors, got %d", len(sender))
   131→	}
   132→	if len(receiver) < 3 {
   133→		return nil, fmt.Errorf("need at least 3 receiver vectors, got %d", len(receiver))
   134→	}
   135→
   136→	reqBody := map[string]any{
   137→		"sender_vectors":   sender,
   138→		"receiver_vectors": receiver,
   139→	}
   140→
   141→	body, err := json.Marshal(reqBody)
   142→	if err != nil {
   143→		return nil, err
   144→	}
   145→
   146→	req, err := http.NewRequestWithContext(ctx, "POST", c.baseURL+"/analyze/bilateral", bytes.NewReader(body))
   147→	if err != nil {
   148→		return nil, err
   149→	}
   150→	req.Header.Set("Content-Type", "application/json")
