   175→// IsAvailable checks if the TheLoom server is reachable.
   176→func (c *Client) IsAvailable(ctx context.Context) bool {
   177→	req, err := http.NewRequestWithContext(ctx, "GET", c.baseURL+"/health", nil)
   178→	if err != nil {
   179→		return false
   180→	}
   181→	resp, err := c.httpClient.Do(req)
   182→	if err != nil {
   183→		return false
   184→	}
   185→	defer resp.Body.Close()
   186→	return resp.StatusCode == http.StatusOK
   187→}
